def psi(x, r, len):
    tmp1 = x^r
    tmp2 = tmp1 - r
    ans = 0
    if tmp2 < 0:
        ans = tmp2 + 2**len
    else :
        ans = tmp2
    return ans % (2**len)

def Goubin(x1, x2, s, r, len):
    a1 = x1 ^ s
    a2 = x2 ^ s
    u = a1 ^ psi(a1, r^a2, len)
    A1 = u ^ psi(a1, r, len)
    A2 = a2
    return A1, A2


def B2A_q(B0, B1, I_r0, I_r1, I_r2, I_r3):
    x0, x1 = Goubin(B0, B1, I_r0, I_r1, 12)
    y0_without = (x0*33556320) >> 24
    y1_without = (x1*10080*3329) >> 24
    y0 = y0_without % 6658
    y1 = y1_without % 6658
    y0 = y0 + 1
    
    b0 = y0 % 2
    b1 = y1 % 2
    a0, a1 = Goubin(b0, b1, I_r2, I_r3, 1)
    z0 = (y0 - a0) % 6658
    z1 = (y1 - a1) % 6658
    c0 = (z0 - z0%2) % 6658
    c1 = (z1 - z0%2) % 6658
    A0 = (c0 >> 1) % (2**12)
    A1 = (c1 >> 1) % (2**12)
    return A0, A1

